def transcricao(fitaDeDNA):
    mRNA = fitaDeDNA.replace("T", "U")
    return mRNA

def salvaEmArquivo(mRNA):
    arquivo = open("RNA Mensageiro.txt", 'w', encoding="utf8")
    arquivo.write(mRNA)
    arquivo.close()

def main():
    fitaDeDNA = ""
    mRNA = transcricao(fitaDeDNA)
    salvaEmArquivo(mRNA)

if __name__ == "__main__":
    main()